import React, { useState, useEffect } from "react";
import bank from "../../../assets/images/mdi_bank.png";
import {VERIFY_ACCOUNT, FETCH_BANK } from "../../../utils/constants";
import Popup from "./PopUp";
import axios from "../../../utils/axiosInstance";
import { Link } from "react-router-dom";
import people from "../../../assets/images/mdi_users-group.png";
import logo from "../../../assets/images/flexbycico.svg";


const MainFundTransferPage = () => {
  const [step, setStep] = useState(1);
  const [tabIndex, setTabIndex] = useState(1);
  const [userInput, setUserInput] = useState("");
  const [popupVisible, setPopupVisible] = useState(false);
  const [suggestedBanks, setSuggestedBanks] = useState([]);
  const [value, setValue] = useState('NGN 0:00');

  const handleAmountChange = (e) => {
    setValue(e.target.value);
  };

  const formTitles = ["Account Number", "Amount", "Summary"];

  const [isChecked, setChecked] = useState(false);

  const handleToggleChange = () => {
    setChecked(!isChecked);
  };

  useEffect(() => {
    if (step === 1 && userInput.length === 10) {
      // Automatically move to the next step when 10 digits are filled
      setStep(step + 1);
      setTabIndex(tabIndex + 1);

      // Fetch banks and show popup
      fetchBanks(userInput);
    }
  }, [step, tabIndex, userInput]);

  const fetchBanks = async (accountNumber) => {
    try {
      // Fetch banks based on the accountNumber
      const res = await axios.get(`${FETCH_BANK}/${accountNumber}`);

      // Extract and display suggested banks in a pop-up
      const suggestedBanks = res.data.data;

      // Show the pop-up with suggested banks
      setSuggestedBanks(suggestedBanks);
      setPopupVisible(true);
    } catch (error) {
      console.error("Error fetching banks:", error);
    }
  };

  let currentStepComponent;

  switch (step) {
    case 1:
      currentStepComponent = (
        <div className="my-7">
          <h1 className="font-extrabold text-3xl my-3">Enter an Account Number</h1>
          <p className="text-xl my-3">Enter the 10 digit bank account number</p>
          <div className="relative">
            <img src={bank} className="absolute top-8 left-1 w-[20px]" alt="bank icon"/>
            <input
              type="number"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Enter account number"
              className="border border-gray-200 py-3 px-10 w-full my-6"
            />
          </div>
        </div>
      );
      break;
    case 2:
      currentStepComponent = (
        <div className="my-7 text-xl">
          <h1 className="font-extrabold text-3xl my-3">Amount</h1>
          <p className="text-xl my-3">Enter the 10 digit bank account number</p>
          <div className="">
           <p>Send to</p>
           <div className="flex justify-between items-center border border-gray-200 rounded-lg my-3 p-4 md:w-[300px] w-full ">
              <div className="flex gap-2 items-center">
              <div className="bg-global-gray p-3 rounded-lg text-color1">ME</div>
                <div>
                  <p>Mark Ekpobiyere</p>
                  <p>GTBank MFB-0118721470</p>
                </div>
              </div>
                
              
                  <Link to='/transfer' className="hover:underline text-color1"> Change</Link>
                

           </div>
            <div className="flex justify-between items-center">
              <div className="flex gap-3 items-center">
              <img src={people} alt="people icon"/>
              <div> Add to beneficiaries</div>
              </div>
              <div>
              <input
        type="checkbox"
        id="toggle"
        checked={isChecked}
        onChange={handleToggleChange}
        className="hidden"
      />
      <label
        htmlFor="toggle"
        className={`cursor-pointer w-14 rounded-full p-1 transition duration-300 ease-in-out ${
          isChecked ? 'bg-purple-500' : ''
        }`}
      >
        <div
          className={`bg-white w-6 h-6 rounded-full shadow-md transform ${
            isChecked ? 'translate-x-6 bg-purple-500' : 'translate-x-0'
          } transition duration-300 ease-in-out`}
        ></div>
      </label>


              </div>
            </div>
          </div>

          <div className="flex justify-between items-center border border-gray-200 rounded-lg my-3 p-4 md:w-[300px] w-full ">
              <div className="flex gap-3 items-center">
              <div className="bg-global-gray p-3 rounded-lg text-color1"><img src={logo} alt="logo" className="w-20"/></div>
                <div>
                  <p>Mark Ekpobiyere</p>
                  <p>GTBank MFB-0118721470</p>
                  <p>Balance-#600,000</p>

                </div>
              </div>
                
              <Link to='/transfer' className="hover:underline text-color1"> </Link>
            

           </div>
           <div className="mb-4 text-xl">
      <label htmlFor="editableInput" className="block text-sm font-medium text-gray-700">
        Amount
      </label>
      <input
        type="text"
        id="editableInput"
        value={value}
        onChange={handleAmountChange}
        className="mt-1 p-2 border font-bold text-xl p-5 rounded-md w-full focus:outline-none focus:ring focus:border-blue-300"
      />
    </div>
    <div className="mb-4 text-xl">
      <label htmlFor="remarks" className="block text-sm font-medium text-gray-700">
        Remarks
      </label>
      <textarea
        id="remarks"
        className="mt-1 p-2 border rounded-md w-full focus:outline-none focus:ring focus:border-blue-300 resize-none"
      ></textarea>
    </div>

    <div>
    <button className="bg-white text-color1 rounded-lg h-14 w-full text-white">
    
    </button>
    <button className="bg-white text-color1 rounded-lg h-14 w-full text-white">
    <Link to='/transfer'> Back </Link>
    </button>
    <button className="bg-white text-color1 rounded-lg h-14 w-full text-white">
    <Link to='/transfer'> Back </Link>
    </button>
    </div>
        </div>      
        
        );
      break;
    case 3:
      currentStepComponent = (
        <h1>Step 3: Additional content for the third step</h1>
      );
      break;
    default:
      currentStepComponent = null;
      break;
  }

  return (
    <div className="rounded-lg mt-10 pt-20">
      <div className="mb-4">
        <div className="relative pt-1">
          <div className="flex">
            <div className="flex flex-row w-full gap-2 justify-center">
              {formTitles.map((title, index) => (
                <div
                  key={index}
                  className={`${
                    index < tabIndex - 1
                      ? "bg-color1 h-2"
                      : index === tabIndex - 1
                      ? "bg-color1 h-2"
                      : "bg-global-gray h-2"
                  } rounded-lg transition-all ease-in-out duration-300`}
                >
                  <div
                    className={`text-2xl md:font-semibold capitalize mx-6 ${
                      index < tabIndex - 1
                        ? "text-black"
                        : index === tabIndex - 1
                        ? "text-black"
                        : "text-gray-300"
                    } -mt-8`}
                  >
                    {title}
                  </div>
                  <div className="text-left py-1.5 pl-2 md:pr-4 h-2 w-48"></div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
      <div className="flex justify-center">
        <div className="flex flex-col">{currentStepComponent}</div>
      </div>

      <Popup
        isVisible={popupVisible}
        suggestedBanks={suggestedBanks}
        onClose={() => setPopupVisible(false)}
      />
    </div>
  );
};

export default MainFundTransferPage;
