const Popup = ({ isVisible, suggestedBanks, onClose }) => {
    const [selectedBanks, setSelectedBanks] = useState([]);
  
    const handleCheckboxChange = (bankCode) => {
      const isSelected = selectedBanks.includes(bankCode);
  
      if (isSelected) {
        setSelectedBanks(selectedBanks.filter((code) => code !== bankCode));
      } else {
        setSelectedBanks([...selectedBanks, bankCode]);
      }
    };
  
    return (
      <>
        {isVisible && (
          <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
            <div className="bg-white p-4 rounded-md">
              <h2 className="text-xl font-bold mb-4">Select Banks:</h2>
              <ul>
                {suggestedBanks.map((bank) => (
                  <li key={bank.code} className="mb-2">
                    <label className="flex items-center">
                      <input
                        type="checkbox"
                        value={bank.code}
                        checked={selectedBanks.includes(bank.code)}
                        onChange={() => handleCheckboxChange(bank.code)}
                        className="mr-2"
                      />
                      {bank.name}
                    </label>
                  </li>
                ))}
              </ul>
              <button
                className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md"
                onClick={() => onClose(selectedBanks)}
              >
                Confirm
              </button>
            </div>
          </div>
        )}
      </>
    );
  };
  
  export default Popup;
