import React, { useState } from "react";
import bank from "../../../assets/images/mdi_bank.png";

const Popup = ({ isVisible, suggestedBanks, onClose }) => {
  const [selectedBank, setSelectedBank] = useState("");
  const [showSearchPopup, setShowSearchPopup] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const handleCheckboxChange = (bankCode) => {
    setSelectedBank(bankCode);
  };

  const handleSearchInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSearchButtonClick = () => {
    setShowSearchPopup(true);
  };

  const handleSearchPopupClose = (selectedBankFromSearch) => {
    setShowSearchPopup(false);
    if (selectedBankFromSearch) {
      onClose(selectedBankFromSearch);
    }
  };

  // Filter suggested banks based on the first two digits of the selected bank code
  const filteredBanks = suggestedBanks.filter(
    (bank) => bank.bankcode.slice(0, 2) === selectedBank.slice(0, 2)
  );

  return (
    <>
      {isVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50 text-center">
          <div className="bg-white p-4 rounded-md md:w-[300px] md:h-[200px]">
            <h2 className="text-2xl font-bold mb-4">
              {showSearchPopup ? "Search Bank" : "Suggested Bank"}
            </h2>
            {showSearchPopup ? (
              <>
                <input
                  type="text"
                  placeholder="Search bank..."
                  value={searchQuery}
                  onChange={handleSearchInputChange}
                  className="border border-gray-300 py-2 px-3 w-full mb-4"
                />
                <button
                  className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md"
                  onClick={() => handleSearchPopupClose(selectedBank)}
                >
                  Confirm
                </button>
              </>
            ) : (
              <>
                <h2 className="text-xl mb-4">
                  Select the most suitable bank
                </h2>
                <ul>
                  {filteredBanks.length > 0 ? (
                    filteredBanks.map((bank) => (
                      <li key={bank.bankcode} className="mb-2">
                        <label className="flex justify-between items-center">
                          {bank.bankName}
                          <input
                            type="radio"
                            value={bank.bankcode}
                            checked={selectedBank === bank.bankcode}
                            onChange={() =>
                              handleCheckboxChange(bank.bankcode)
                            }
                            className="mr-2"
                          />
                        </label>
                      </li>
                    ))
                  ) : (
                    <p>No suggested banks based on the selected bank.</p>
                  )}
                </ul>
                <button
                  className="flex gap-3 items-center bg-global-gray rounded-lg py-4 my-6"
                  onClick={handleSearchButtonClick}
                >
                  <img src={bank} alt="bank icon" className="w-10" />
                  <div className="text-xl font-semibold">Search Bank</div>
                </button>
              </>
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default Popup;
