import React, { useState, useEffect, useRef } from "react";
import axios from "../../utils/axiosInstance";
import { useToasts } from "react-toast-notifications";
import ContactDetail from "../../components/contactDetails/contactDetail";


const SettingsForm = () => {
  const [otp, setOtp] = useState("");
  const biodataRef = useRef('');
  const [step, setStep] = useState(1);
  const [tabIndex, setTabIndex] = useState(1);
  const { addToast } = useToasts();
  const [payload, setPayload] = useState({
    email: "",
    password: {
      old_password: "",
      new_password: "",
      confirm_password: "",
    },
    image: "",
  });
  const [docUploadPayload, setDocUploadPayload] = useState("");
  const [pinPayload, setPinPayload] = useState({
    agent_id: "",
    transaction_pin: "",
  });
  // const [userData, setUserData] = useState('');
  // const [showPassword, setShowPassword] = useState(false);
  // const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [countries, setCountries] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [states, setStates] = useState([]);
  const [selectedState, setSelectedState] = useState("");
  const [selectedFiles, setSelectedFiles] = useState('');
  const [selectedDocument, setSelectedDocument] = useState("");
  const [documentImage, setDocumentImage] = useState('');
  const [utilityImage, setUtilityImage] = useState('');
  const [docImage, setDocImage] = useState('');
  const [fileUploaded, setFileUploaded] = useState(false);
  const [guarantorSelect, setGuarantorSelect] = useState('');
  const [pin, setPin] = useState([]);
  const [confirmPin, setConfirmPin] = useState([]);


  const [selectedImage, setSelectedImage] = useState('');
  const fileInputRef = useRef('');
  const pinInputRefs = [useRef(), useRef(), useRef(), useRef()];
  const confirmPinInputRefs = [useRef(), useRef(), useRef(), useRef()];
  const utilityBillInputRef = useRef();
  const idDocumentInputRef = useRef();
  const [loading, setLoading] = useState('');


  // business_address
  // document_type
  // document_image
