import React, { useState, useEffect } from 'react';

const MainFundTransferPage = () => {
  const [step, setStep] = useState(1);
  const [tabIndex, setTabIndex] = useState(1);
  const [userInput, setUserInput] = useState('');

  const formTitles = ['Account Number', 'Amount', 'Summary'];

  useEffect(() => {
    if (step === 1 && userInput.trim() !== '') {
      // Automatically move to the next step when input in the first step is completed
      setStep(step + 1);
      setTabIndex(tabIndex + 1);
    }
  }, [step, tabIndex, userInput]);

  const isLastStep = step === 3;
  const isFirstStep = tabIndex === 1 && step === 1;

  let currentStepComponent;

  switch (step) {
    case 1:
      currentStepComponent = (
        <div>
          <h1 className='font-bold text-2xl '>Enter an Account Number</h1>
          <p>Enter the 10 digit bank account number</p>
          <input
            type="number"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            placeholder='Enter account number'
            cl
          />
        </div>
      );
      break;
    case 2:
      currentStepComponent = <h1>Step 2: Additional content for the second step</h1>;
      break;
    case 3:
      currentStepComponent = <h1>Step 3: Additional content for the third step</h1>;
      break;
    default:
      currentStepComponent = null;
      break;
  }

  return (
    <div className="rounded-lg mt-10 pt-20">
      <div className="mb-4">
        <div className="relative pt-1">
          <div className="flex">
            <div className="flex flex-row w-full gap-2 justify-center">
              {formTitles.map((title, index) => (
                <div
                  key={index}
                  className={`${index < tabIndex - 1
                    ? 'bg-color1 h-2'
                    : index === tabIndex - 1
                    ? 'bg-color1 h-2'
                    : 'bg-global-gray h-2'
                  } rounded-lg transition-all ease-in-out duration-300`}
                >
                  <div
                    className={`text-sm md:font-semibold capitalize ${index < tabIndex - 1
                      ? 'text-black'
                      : index === tabIndex - 1
                      ? 'text-black'
                      : 'text-global-gray'
                    } -mt-6`}
                  >
                    {title}
                  </div>
                  <div className="text-left py-1.5 pl-2 md:pr-4 h-2 w-48"></div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
      <div className="flex justify-center">
        <div className="flex flex-col">
          {currentStepComponent}
        </div>
      </div>
    </div>
  );
};

export default MainFundTransferPage;
