import React, { useState } from "react";

const Popup = ({ isVisible, suggestedBanks, onClose }) => {
  const [selectedBank, setSelectedBank] = useState("");

  const handleCheckboxChange = (bankCode) => {
    setSelectedBank(bankCode);
  };

  // Filter suggested banks based on the first two digits of the selected bank code
  const filteredBanks = suggestedBanks.filter(
    (bank) => bank.bankcode.slice(0, 2) === selectedBank.slice(0, 2)
  );

  return (
    <>
      {isVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50 md:w-[300px] ">
          <div className="bg-white p-4 rounded-md">
            <h2 className="text-xl font-bold mb-4">Select Bank:</h2>
            <ul>
              {suggestedBanks.map((bank) => (
                <li key={bank.bankcode} className="mb-2">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      value={bank.bankcode}
                      checked={selectedBank === bank.bankcode}
                      onChange={() => handleCheckboxChange(bank.bankcode)}
                      className="mr-2"
                    />
                    {bank.bankName}
                  </label>
                </li>
              ))}
            </ul>
            {selectedBank && (
              <button
                className="mt-4 bg-blue-500 text-white px-4 py-2 rounded-md"
                onClick={() => onClose(selectedBank)}
              >
                Confirm
              </button>
            )}
          </div>
        </div>
      )}
    </>
  );
};

export default Popup;
