import React, { useState } from "react";
import bank from "../../../assets/images/mdi_bank.png";

const Popup = ({ isVisible, suggestedBanks, onClose }) => {
  const [selectedBank, setSelectedBank] = useState("");

  const handleCheckboxChange = (bankCode) => {
    setSelectedBank(bankCode);
    onClose(selectedBank)
  };

  // Filter suggested banks based on the first two digits of the selected bank code
  const filteredBanks = suggestedBanks.filter(
    (bank) => bank.bankcode.slice(0, 2) === selectedBank.slice(0, 2)
  );

  return (
    <>
      {isVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50 ">
          <div className="bg-white p-4 rounded-md  md:w-[300px] md:h-[200px]">
            <h2 className="text-2xl font-bold mb-4">Suggested Bank</h2>
            <h2 className="text-xl mb-4">Select the most suitable bank</h2>

            <ul>
              {suggestedBanks.map((bank) => (
                <li key={bank.bankcode} className="mb-2">
                  <label className="flex items-center">
                    <input
                      type="radio"
                      value={bank.bankcode}
                      checked={selectedBank === bank.bankcode}
                      onChange={() => handleCheckboxChange(bank.bankcode)}
                      className="mr-2"
                    />
                    {bank.bankName}
                  </label>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </>
  );
};

export default Popup;
