import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye, faEyeSlash } from "@fortawesome/free-solid-svg-icons";

const TransactionPinPopup = ({ isVisible, onClose, onPinEntered }) => {
  const [pin, setPin] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const handlePinChange = (e) => {
    setPin(e.target.value);
  };

  const handleTogglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  const handleConfirm = () => {
    onPinEntered(pin);
    onClose();
  };

  return (
    <>
      {isVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50 text-center">
          <div className="bg-white p-4 rounded-md md:w-[400px]">
            <h2 className="text-2xl font-bold mb-4">Enter Transaction Pin</h2>
            <p className="my-3 text-xl ">To complete this transfer, please enter your 4-Digit Transaction  PIN</p>
            <div className="relative mb-4">
              <input
                type={showPassword ? "text" : "password"}
                value={pin}
                onChange={handlePinChange}
                placeholder="Enter your pin"
                className="border border-gray-200 py-3 px-4 w-full pr-12"
              />
              <button
                className="absolute right-4 top-1/2 transform -translate-y-1/2"
                onClick={handleTogglePasswordVisibility}
              >
                <FontAwesomeIcon
                  icon={showPassword ? faEye : faEyeSlash}
                  className="text-gray-500 cursor-pointer"
                />
              </button>
            </div>
            <button
              onClick={handleConfirm}
              className="bg-color1 text-white rounded-lg h-12 w-full"
            >
              Confirm
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default TransactionPinPopup;
