import React from 'react';
import { useHistory } from 'react-router-dom';
import SettingsForm from './settingsForm';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faWrench } from '@fortawesome/free-solid-svg-icons';
import settings from '../../assets/images/download.png';

const Settings = () => {
  const history = useHistory();

  const handleLinkClick = (hash) => {
    history.push(`/settings${hash}`);
  };

  return (
    <div className='m-0'>
      <div className='flex items-center gap-10'>
        <img src={settings} alt="setting icon" className='w-100'/>
        <div>
          <h1>Settings</h1>
          <h2>Manage your account settings and preferences</h2>
        </div>
      </div>
      <div className='bg-white h-full'>
        <div className="p-5">
          <div
            onClick={() => handleLinkClick('#pin')}
            className="flex items-center relative  text-no-underline text-purple-800 text-lg"
          >
            {/* ... your content for Update transaction pin */}
          </div>
          <div
            onClick={() => handleLinkClick('#biodata')}
            className="flex items-center relative  no-underline text-purple-800 text-lg"
          >
            {/* ... your content for Edit Profile */}
          </div>
          <div
            onClick={() => handleLinkClick('#contact')}
            className="flex items-center relative  no-underline text-purple-800 text-lg"
          >
            {/* ... your content for Change Password */}
          </div>
          {/* ... other links */}
          <div
            onClick={() => handleLinkClick('#contact')}
            className="flex items-center relative  no-underline text-purple-800 text-lg"
          >
            {/* ... your content for Change Password */}
          </div>
        </div>

        {/* Render SettingsForm without any changes */}
        <SettingsForm />
      </div>
    </div>
  );
};

export default Settings;
