import React, { useEffect, useRef } from 'react';
import SettingsForm from './settingsForm';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faWrench } from '@fortawesome/free-solid-svg-icons';
import settings from '../../assets/images/download.png'
import { useLocation } from 'react-router-dom';

const Settings = () => {
  const location = useLocation();
  const containerRef = useRef(null);

  useEffect(() => {
    // Check the pathname in the URL and scroll to the corresponding section
    const pathname = location.pathname.replace('/settings/', '');
    console.log('Current pathname:', pathname);

    if (containerRef.current) {
      const targetElement = containerRef.current.querySelector(`#${pathname}`);
      if (targetElement) {
        targetElement.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }, [location.pathname]);

  return (
    <div className='m-0'>
        <div className='flex items-center gap-10'>
            <img src={settings} alt="setting icon" className='w-100'/>
        <div>
            <h1>Settings</h1>
        <h2>Manage your account settings and preferences</h2>
        </div>
        </div>
        <div className='bg-white h-full' ref={containerRef}>
        <SettingsForm/>

        </div>
    </div>
  )
}

export default Settings
