import React, { useState } from "react";
import bank from "../../../assets/images/mdi_bank.png";

const SearchInputPopup = ({ isVisible, onClose, onSearch }) => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = () => {
    onSearch(searchQuery);
    onClose();
  };

  return (
    <div>
      {isVisible && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50 text-center">
        <div className="bg-white p-6 rounded-md md:w-[400px] absolute md:top-4/5 top-[200px] md:left-3/5 left-2/5">
          <div className="flex justify-between items-center">
          <h2 className="text-2xl font-bold mb-4">Select a Bank</h2>
          <div><button onClick={()=>(onClose())} className="text-2xl font-bold">x</button></div>
          </div>
          <p>Which bank does your recipient use?</p>
          <div className="relative">
          <img src={bank} alt="bank icon" className="w-10" />

          </div>
            
            <button
              onClick={handleSearch}
              className="bg-color1 text-white rounded-lg h-12 w-full"
            >
              Search
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchInputPopup;
